        /// <summary>
        /// 添加个人信息的方法
        /// </summary>
        /// <param name="name">姓名</param>
        /// <param name="stuNum">学号</param>
        /// <param name="courseName">课程名</param>
        public void AddInfomation(string name, string stuNum, string courseName)
        {
            System.Uri web_uri = new System.Uri(string.Format("http://{0}/ksweb/Pic.aspx?userid={1}", FormConnInfo.web_ip, FormConnInfo.StudentNum));
            webBrowPictrue.Url = web_uri;
            this.lblStulName.Text += name;
            this.lblStuNum.Text += stuNum;
            //this.lblStuType.Text += courseName;
            this.rtbCousename.Text = courseName;
            //调整信息panel位置
            this.lblStulName.Top = this.webBrowPictrue.Bottom + 20;
            this.lblStuNum.Top = this.lblStulName.Bottom + 20;
            this.lblStuType.Top = this.lblStuNum.Bottom + 20;
            this.rtbCousename.Top = this.lblStuType.Bottom + 2;
            this.rtbCousename.Height = 90;
            this.lblUpan.Top = this.rtbCousename.Bottom + 20;
            this.lblstu_phone.Top = this.lblUpan.Bottom + 20;
            this.txtstu_num.Top = this.lblstu_phone.Bottom + 10;
            this.toumingPanel.Top = this.txtstu_num.Bottom + 10;//调整透明panel的位置
            this.toumingPanel.Left = this.txtstu_num.Left;
            this.toumingPanel.Anchor = System.Windows.Forms.AnchorStyles.Bottom | System.Windows.Forms.AnchorStyles.Top |
                System.Windows.Forms.AnchorStyles.Left;
            if (HaveChoiceandBlank)
            {
                lblReminder.Text = System.String.Format("本大题共有{0}小题，每题{1}分，共{2}分", choice_num, choice_score, choice_score * choice_num);
                this.webBrowerTimu.DocumentText = (radio_numCount + 1).ToString() + ": " + radiochioce_timu[radio_numCount];
                this.webBrowAnsA.DocumentText = radio_optionA[radio_numCount];
                this.webBrowAnsB.DocumentText = radio_optionB[radio_numCount];
                this.webBrowAnsC.DocumentText = radio_optionC[radio_numCount];
                this.webBrowAnsD.DocumentText = radio_optionD[radio_numCount];
            }
        }
